import { Component } from '@angular/core';
import { FundingBasket } from 'src/app/funding.basket';
import { Needs } from 'src/app/needs';

@Component({
  selector: 'app-helper-page',
  templateUrl: './helper-page.component.html',
  styleUrls: ['./helper-page.component.scss']
})
export class HelperPageComponent {
  fundingBasket: FundingBasket;

  constructor() {
    this.fundingBasket = new FundingBasket(cupboard);
  }

  addNeedToBasket(need: Needs): void {
    this.fundingBasket.addNeedToBasket(need);
  }

  removeNeedFromBasket(need: Needs): void {
    this.fundingBasket.removeSupportedNeed(need);
  }

  checkout(): void {
    this.fundingBasket.checkOut();
  }
}
<!doctype html>
<html lang="en">
<head>
  <meta charset="utf-8">
  <title>Helper</title>
  <base href="/helper">
  <meta name="helper" content="width=device-width, initial-scale=1">

  <link rel="stylesheet" 
          type="text/scss" 
          href="helper-page.component.scss">

</head>

<h1> 
    List of Needs. Please choose, helper!
</h1>

<div *ngIf="fundingBasket.getSupportedNeeds().length > 0">
  <h2>Supported Needs: </h2>
  <ul>
    <li *ngFor="let need of fundingBasket.getSupportedNeeds()">
      {{ need.getName() }} - {{ need.getQuantity() }} - <button (click)="removeNeedFromBasket(need)">Remove</button>
    </li>
  </ul>
  <button (click)="checkout()">Checkout</button>
</div>

<h2> Add Needs: </h2>
<ul>
  <li *ngFor = "let need of needsList">
    {{ need.getName() }} - {{ need.getQuantity() }} - <button (click)="addNeedToBasket(need)">Add</button>
  </li>
</ul>
