import { Component, OnInit } from '@angular/core';
import { Needs } from 'src/app/needs';
import { HelperService } from 'src/app/helper.service';
import { Cupboard } from 'src/app/cupboard.service';
import { LoginComponent } from 'src/app/login/login.component';
import { Notification } from 'src/app/notification';
import { NotificationService } from 'src/app/notification.service';

@Component({
  selector: 'app-helper-page',
  templateUrl: './helper-page.component.html',
  styleUrls: ['./helper-page.component.scss']
})
export class HelperPageComponent implements OnInit {

  cupboardNeeds: Needs[] = [];
  fundingBasket: Needs[] = [];
  supportedNeeds: Needs[] = [];
  selectedNeed?: Needs;
  searchTerm: string = '';
  currentUser: string = '';

  constructor(private helperService: HelperService, private adminService: admin, 
    private notificationService: NotificationService) {}

  ngOnInit(): void {
    this.getCupboardNeeds();
    this.currentUser = LoginComponent.currentUser
  }

  getCupboardNeeds(): void {
    this.cupboardService.getAllNeeds().subscribe(needs => this.cupboardNeeds = needs);
  }


  searchCupboardNeeds(): void {
    this.cupboardService.searchPartialName(this.searchTerm).subscribe(needs => this.cupboardNeeds = needs);
  }

  resetCupboardSearch(): void {
    this.searchTerm = '';
    this.getCupboardNeeds();
  }

  addToBasket(cupboardNeed: Needs): void {
    if (cupboardNeed.quantity > 0) {
      this.helperService.addSupportedNeed(cupboardNeed).subscribe(
        (addedNeed: Needs) => {
          const originalBasketIndex = this.supportedNeeds.findIndex(n => n.name === addedNeed.name);
          if (originalBasketIndex !== -1) {
            this.supportedNeeds[originalBasketIndex].quantity += 1;
          } else {
            this.supportedNeeds.push({
              name: addedNeed.name,
              cost: addedNeed.cost,
              type: addedNeed.type,
              quantity: 1,
            });
          }
  
          const foundNeed = this.cupboardNeeds.find(n => n.name === cupboardNeed.name);
          if (foundNeed) {
            foundNeed.quantity -= 1;
          }
  
          const message = `Adding need to the basket`;
          alert(message);
  
        },
        error => {
          console.error(error);
          const message = `Error adding need to the basket`;
          alert(message);
        }
      );
    } else {
      const message = `Cannot add need to the basket since the quantity is already at 0.`;
      alert(message);
    }
  }  

  removeFromBasket(need: Needs): void {
    this.helperService.removeSupportedNeed(need.name).subscribe(
      _ => {
        const originalNeed = this.cupboardNeeds.find(n => n.name === need.name);
        if (originalNeed) {
          originalNeed.quantity += 1;

          this.supportedNeeds = this.supportedNeeds.filter(n => n.name !== need.name);

          const message = `Removing need from basket`;
          alert(message);

          this.cupboardService.removeFromBasket(need);
        } else {
          const message = `Original need not found in cupboard`;
          alert(message);
        }
      },
      error => {
        console.error(error);
        const message = `Error removing need from basket`;
        alert(message);
      }
    );
  }

  checkout(): void {
    this.helperService.checkOut().subscribe(
      _ => {
        this.supportedNeeds = [];
        this.getCupboardNeeds();

        const message = 'Checkout Successful!';
        alert(message);
      },
      error => {
        console.error(error);
        const message = 'Error during checkout';
        alert(message);
      }
    );
  }

  createNotification(){
    let username = this.currentUser
    if(this.fundingBasket.length!=0){
      this.fundingBasket.forEach(fundneed => 
      this.notificationService.createNotification
      ({username,"need": fundneed.name,"quantity": fundneed.quantity} as Notification));
    }
  }
}
