import { Component, OnInit } from '@angular/core';

import { HelperMessage } from 'src/app/HelperMessage';
import { HelperMessageService } from 'src/app/helper-message.service';

@Component({
  selector: 'app-landing',
  templateUrl: './landing.component.html',
  styleUrls: ['./landing.component.scss']
})
export class LandingComponent implements OnInit{
helperMessages: HelperMessage[] = []
helperMessage: HelperMessage = {"id": 0, "username":"temp","message":"temp"}


constructor(private messageService: HelperMessageService){}

ngOnInit(): void {
    this.getAllMessages();
}

getAllMessages(): void{
  this.messageService.getAllMessages().subscribe(messages=> this.helperMessages = messages)
}

getRandomMessage(): void{
let currentMessageId = this.getRandomInt(0,this.helperMessages.length);
this.messageService.searchMessageId(currentMessageId).subscribe(message => this.helperMessage = message);


}
//gets random integer including min excluding max
getRandomInt(min: number, max: number) {
  min = Math.ceil(min);
  max = Math.floor(max);
  return Math.floor(Math.random() * (max - min) + min);
}
}
