import { Injectable, OnInit } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http'

import { Observable, of } from 'rxjs';
import { catchError, map, tap } from 'rxjs/operators';

import { helperAccount } from './helperAccount';
import { UserAccount } from './UserAccount';

@Injectable({
  providedIn: 'root'
})
export class AuthService{

  private accountsUrl = 'http://localhost:8080/account';  // URL to web api
  currentUsername: String = "";

  httpOptions = {
    headers: new HttpHeaders({ 'Content-Type': 'application/json' })
  };
  

  constructor(
    private http: HttpClient
    ) { }

  login(string ){
    this.getAccount(this.form.value.username).subscribe(name =>
      this.currentUsername = name.username);
      if (this.currentUsername === ""){
        alert(this.currentUsername)
      } else if (this.currentUsername === "admin"){
        alert(this.currentUsername)
        this.router.navigateByUrl('/admin');
      } else {
        this.router.navigateByUrl('/helper');
      }
  }
  

  getAccounts(): Observable<UserAccount[]> {
    return this.http.get<UserAccount[]>(this.accountsUrl)
      .pipe(
        tap(_ =>catchError(this.handleError<UserAccount[]>('getAccounts', [])
      )));
  }

  getAccount(accountName: string):Observable<UserAccount> {
    const url = `${this.accountsUrl}/${accountName}`;
    return this.http.get<UserAccount>(url)
      .pipe(
        tap(_ =>catchError(this.handleError<UserAccount>('getAccount')
      )));
      }

  createUser(account: helperAccount): Observable<helperAccount> {
    return this.http.post<helperAccount>(this.accountsUrl, account, this.httpOptions).pipe(
      tap((newAccount: helperAccount) =>
      catchError(this.handleError<string>('createUser'))
    ));
  }

  private handleError<T>(operation = 'operation', result?: T) {
    return (error: any): Observable<T> => {

      //TODO: send the error to remote logging infrastructure
      console.error(error); // log to console instead

      //Let the app keep running by returning an empty result.
      return of(result as T);
    }
  }

}
