import { Component, OnInit } from '@angular/core';
import { AdminService } from 'src/app/admin.service';
import { Needs } from 'src/app/needs';
import { FormGroup, FormBuilder, Validators, FormControl } from '@angular/forms';
import { min } from 'rxjs';

@Component({
  selector: 'app-admin-page',
  templateUrl: './admin-page.component.html',
  styleUrls: ['./admin-page.component.scss']
})
export class AdminPageComponent implements OnInit{
  cupboardNeeds: Needs[] = [];
  form: FormGroup = this.fb.group({
    needName: ['',Validators.required],
    needCost: new FormControl('',Validators.required, Validators.pattern
    needType: ['',Validators.required],
    needQuant: ['',Validators.required]
  });

  constructor(private adminService: AdminService,private fb: FormBuilder){ };

  ngOnInit(): void {
      this.getHeroes();
  }

  getHeroes(): void {
    this.adminService.getAccounts()
    .subscribe(Needs => this.cupboardNeeds = Needs);
  }

  add(needName: string, needCost: string, needType:string, needQuant:string): void{
    needName = needName.trim();
    needType = needType.trim();
    needCost = needCost.trim();
    needQuant = needQuant.trim();

    let costDouble = Number(needCost);
    let quantInt = parseInt(needQuant);

    let needToAdd = {} as Needs;

    needToAdd.name = needName;
    needToAdd.cost = costDouble;
    needToAdd.type = needType;
    needToAdd.quantity = quantInt;

    this.adminService.addNeed(needToAdd);

  }
}
<!doctype html>
<html lang="en">
<head>
  <meta charset="utf-8">
  <title>Admin</title>
  <base href="/admin">
  <meta name="Admin" content="width=device-width, initial-scale=1">

  <link rel="stylesheet" 
          type="text/scss" 
          href="admin-page.component.scss">
</head>

<h2>Admin Cupboard</h2>

<div id="container" [formGroup]="form">
  <!--Need Name input-->
  <label for="new-need">Need name: </label>
  <input type = "text" formControlName="needName" id="new-need" #needName />
  <!--Need Cost input-->
  <label for="new-cost">Need Cost: </label>
  <input type = "number" formControlName="needCost" id="new-cost" #needCost />
  <!--Need Type input-->
  <label for="new-type">Need Type: </label>
  <input type = "text" formControlName="needType" id="new-type" #needType />
  <!--Need Quantity input-->
  <label for="new-quant">Need Quantity: </label>
  <input type = "number" formControlName="needQuant" id="new-quant" #needQuant />

  <!-- (click) passes input value to add() and then clears the input -->
  <button type="button" class="add-button" (click)="add(needName.value,needCost.value,needType.value,needQuant.value);
   needName.value='';needCost.value='';needType.value='';needQuant.value=''" [disabled]="!form.valid">
    Add New Need
  </button>
</div>
