import { Component, OnInit, ɵɵqueryRefresh } from '@angular/core';
import { AuthService } from '../auth.service';
import { FormGroup, FormBuilder, Validators } from '@angular/forms';
import { Router } from '@angular/router'

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss']
})
export class LoginComponent {
  loginValid: boolean = false;
  form: FormGroup = this.fb.group({
    username: ['',Validators.required]
  });
  constructor(private authService: AuthService, private fb: FormBuilder, private router: Router){}

  login(){
    this.authService.getAccount(this.form.value.username).subscribe(name => {if(name.username!=null){this.loginValid=true}});
      if (this.loginValid === false){
        alert("loginValid is false")
      } else if (this.form.value.username === "admin"){
        alert("loginValid is true")
        this.router.navigateByUrl('/admin');
      } else {
        alert("loginValid is true")
        this.router.navigateByUrl('/helper');
      }
    };
}
