import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class AuthService {
  users: any[] = [
    {
      id: 1,
      name: 'Admin',
      username: 'admin'
    },
    {
      id: 2,
      name: 'Sam',
      username: 'sam'
    },
    {
      id: 3,
      name: 'Kelvin',
      username: 'kelvin'
    },
    {
      id: 4,
      name: 'Helper',
      username: 'helper'
    }
  ];
  session: any;

  constructor() { }

  login(username: string){
    let user = this.users.find((u)=>u.username===username);
    if(user){
      this.session = user;
      localStorage.setItem('session', JSON.stringify(this.session))
    }
    return user;
  }
}
