import { Component } from '@angular/core';
import { Needs } from 'src/app/needs';
import { FundingBasket } from 'src/app/fundingbasket.service';
import { NEEDS } from 'src/app/mock-needs';

@Component({
  selector: 'app-helper',
  templateUrl: './helper.component.html',
  styleUrls: ['./helper.component.scss']
})
export class HelperComponent {
    title = 'Helper';
    needs: Needs[] = NEEDS;
    selectedNeed?: Needs;
    searchTerm: string = '';
    showBasket = false;

    constructor(public fundingBasket: FundingBasket) {
    }
  
    addNeedToBasket(need: Needs): void {
      this.fundingBasket.addSupportedNeed(need);
    }
    
    removeNeedFromBasket(need: Needs): void {
      this.fundingBasket.removeSupportedNeed(need.name);
    }
    
    checkout(): void {
      this.fundingBasket.checkOut();
    }
    
    getAllNeeds(): void {
      this.fundingBasket.getSupportedNeeds().subscribe({
        next: (needs: Needs[]) => {
          this.needs = needs;
        },
        error: (error) => {
          console.error('Error fetching needs:', error);
        }
      });
    }

    onSelect(need: Needs): void {
      this.selectedNeed = need;
    }
  
    search(): void {
      if (!this.searchTerm.trim()) {
        this.getAllNeeds();
        return;
      }
  
      this.fundingBasket.searchNeeds(this.searchTerm)
        .subscribe({
          next: (result) => {
            this.needs = result;
          },
          error: (error) => {
            console.error('Error searching needs:', error);
          }
        });
    }

    resetSearch(): void {
      this.searchTerm = '';
      this.getAllNeeds();
    }
}
