import { Component, OnInit } from '@angular/core';
import { Needs } from 'src/app/needs';
import { FundingBasket } from 'src/app/fundingbasket.service';
import { NEEDS } from 'src/app/mock-needs';

@Component({
  selector: 'app-helper-page',
  templateUrl: './helper-page.component.html',
  styleUrls: ['./helper-page.component.scss']
})
export class HelperPageComponent implements OnInit {

  needs: Needs[] = NEEDS;
  selectedNeed?: Needs;
  searchTerm: string = '';
  supportedNeeds: Needs[] = [];

  constructor(public fundingBasket: FundingBasket) {
  }

  ngOnInit(): void {
    this.getSupportedNeeds();
  }

  showNeedDetails(need: Needs): void {
    this.selectedNeed = need;
  }

  onSelect(need: Needs): void {
    this.selectedNeed = need;
  }

  getSupportedNeeds(): void {
    this.fundingBasket.getSupportedNeeds().subscribe(needs => {
      this.supportedNeeds = needs;
    });
  }

  search(): void {
    if (!this.searchTerm.trim()) {
      this.getSupportedNeeds();
      return;
    }

    const searchTermLowerCase = this.searchTerm.toLowerCase();
    this.needs = NEEDS.filter(need =>
      need.name.toLowerCase().includes(searchTermLowerCase)
    );
  }

  resetSearch(): void {
    this.searchTerm = '';
    this.getSupportedNeeds(); 
  }

  addToBasket(need: Needs): void {
    if (need.quantity > 0) {
      const existingNeed = this.supportedNeeds.find(n => n.name === need.name);

      if (existingNeed) {
        existingNeed.quantity += 1;
      } else {
        const newNeed = { ...need, quantity: 1 };
        this.supportedNeeds.push(newNeed);
      }

      this.fundingBasket.addSupportedNeed(need);

      need.quantity -= 1;

      if (need.quantity === 0) {
        const index = this.needs.indexOf(need);
        if (index !== -1) {
          this.needs.splice(index, 1);
        }
      }

      const message = `Adding need to the basket`;
      alert(message);
    } else {
      const message = `Cannot add need to the basket since the quantity is already at 0.`;
      alert(message);
    }
  }

  removeFromBasket(need: Needs): void {
    const existingNeedIndex = this.supportedNeeds.findIndex(n => n.name === need.name);

    if (existingNeedIndex !== -1) {
      this.fundingBasket.removeSupportedNeed(need.name);

      const originalNeed = NEEDS.find(n => n.name === need.name);

      if (originalNeed) {
        originalNeed.quantity += 1;

        // Check if the need is in the list of needs
        const existingListNeedIndex = this.needs.findIndex(n => n.name === need.name);

        if (existingListNeedIndex !== -1) {
          // Need is already in the list, update the quantity
          this.needs[existingListNeedIndex].quantity += 1;
        } else {
          const clonedNeed: Needs = { ...originalNeed };
          this.needs.push(clonedNeed);
        }

        need.quantity -= 1;

        if (need.quantity === 0) {
          this.supportedNeeds.splice(existingNeedIndex, 1);
        }

        const message = `Removing need from basket`;
        alert(message);
      } else {
        const message = `Unable to find original need in the list`;
        alert(message);
      }
    } else {
      const message = `Unable to remove since not in Funding Basket`;
      alert(message);
    }
  }  

  checkout(): void {
    this.fundingBasket.checkOut();
    this.supportedNeeds = [];
    const message = 'Checkout Successful!';
    alert(message);
  }

}
