import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Observable, of } from 'rxjs';
import { catchError, tap } from 'rxjs/operators';
import { Needs } from './needs';

@Injectable({
  providedIn: 'root',
})
export class HelperService {
  private baseUrl = 'http://localhost:8080'; 
  private helperUrl = `${this.baseUrl}/helper`;

  httpOptions = {
    headers: new HttpHeaders({ 'Content-Type': 'application/json' }),
  };

  constructor(private http: HttpClient) {}

  getAllNeeds(): Observable<Needs[]> {
    return this.http.get<Needs[]>(`${this.helperUrl}`, this.httpOptions).pipe(
      tap(_ => catchError(this.handleError<Needs[]>('getAllNeeds', [])))
    );
  }

  addSupportedNeed(needName: string, quant: number): Observable<Needs> {
    const body = { need_name: needName, quant: quant };
    return this.http.post<Needs>(`${this.helperUrl}`, body, this.httpOptions).pipe(
      tap(_ => {}),
      catchError(this.handleError<Needs>('addSupportedNeed'))
    );
  }  

  removeSupportedNeed(needName: string): Observable<Needs> {
    return this.http.delete<Needs>(`${this.helperUrl}/${needName}`, this.httpOptions).pipe(
      tap(_ => catchError(this.handleError<Needs>('removeSupportedNeed')))
    );
  }

  checkOut(): Observable<any> {
    return this.http.put<any>(`${this.helperUrl}`, null, this.httpOptions).pipe(
      tap(_ => catchError(this.handleError<any>('checkOut')))
    );
  }

  searchPartialName(searchText: string): Observable<Needs[]> {
    return this.http.get<Needs[]>(`${this.helperUrl}/?searchText=${searchText}`, this.httpOptions).pipe(
      tap(_ => catchError(this.handleError<Needs[]>('searchPartialName', [])))
    );
  }

  private handleError<T>(operation = 'operation', result?: T) {
    return (error: any): Observable<T> => {
      console.error(error);
      return of(result as T);
    };
  }
}
