import { Component, OnInit } from '@angular/core';
import { NEEDS } from 'src/app/mock-needs'; 
import { Cupboard } from 'src/app/cupboard.service';
import { Needs } from '../needs';

@Component({
  selector: 'app-cupboard',
  templateUrl: './cupboard.component.html',
  styleUrls: ['./cupboard.component.scss']
})
export class CupboardComponent implements OnInit {
  needs: Needs[] = NEEDS;
  selectedNeed?: Needs;

  constructor(private cupboardService: Cupboard) {}

  ngOnInit(): void {
    this.getAllNeeds();
  }

  getAllNeeds(): void {
    this.cupboardService.getAllcupboardNeeds().subscribe({
      next: (needs: Needs[]) => {
        this.needs = needs.slice(0, 5);
      },
      error: (error) => {
        console.error('Error fetching cupboard needs:', error);
      }
    });
  }

  showNeedDetails(need: Needs): void {
    this.selectedNeed = need;
  }
}
<!DOCTYPE html>
<html lang="en">

<head>
  <meta charset="utf-8">
  <title>Needs</title>
  <base href="/helper">
  <meta name="helper" content="width=device-width, initial-scale=1">
  <link rel="stylesheet" type="text/scss" href="cupboard.component.scss">
</head>

<body>
  <h2>Needs</h2>
  <ul class="needs">
    <li *ngFor="let need of needs">
      <button [class.selected]="need === selectedNeed" type="button" (click)="showNeedDetails(need)">
        <span class="badge">{{need.name}}</span>
        <span class="name">{{need.name}}</span>
      </button>
    </li>
  </ul>

  <div *ngIf="selectedNeed">
    <h2>{{selectedNeed.name | uppercase}} Details</h2>
    <div>Cost: {{selectedNeed.cost}}</div>
    <div>Type: {{selectedNeed.type}}</div>
    <div>Quantity: {{selectedNeed.quantity}}</div>
  </div>

</body>

</html>
