import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Observable, of } from 'rxjs';
import { catchError, tap } from 'rxjs/operators';
import { Needs } from 'src/app/needs'; 
import { Cupboard } from 'src/app/cupboard'

@Injectable({
  providedIn: 'root',
})
export class FundingBasket {
  private baseUrl = 'http://localhost:8080/ufund'
  private needsUrl = `${this.baseUrl}/needs`;
  private supportedNeeds: Needs[] = [];
  private cupboard: Cupboard;

  httpOptions = {
    headers: new HttpHeaders({ 'Content-Type': 'application/json' }),
  };

  constructor(private http: HttpClient) {}

  /** GET all needs from the server */
  getAllNeeds(): Observable<Needs[]> {
    return this.http.get<Needs[]>(this.needsUrl).pipe(
      tap((_) => this.log('fetched needs')),
      catchError(this.handleError<Needs[]>('getAllNeeds', []))
    );
  }

  /** POST: add a new need to the server */
  addNeed(need: Needs): Observable<Needs> {
    return this.http.post<Needs>(this.needsUrl, need, this.httpOptions).pipe(
      tap((newNeed: Needs) => this.log(`added need with name=${newNeed.name}`)),
      catchError(this.handleError<Needs>('addNeed'))
    );
  }

  /** PUT: update the need on the server */
  updateNeed(need: Needs): Observable<any> {
    const url = `${this.needsUrl}/${need.name}`;
    return this.http.put(url, need, this.httpOptions).pipe(
      tap((_) => this.log(`updated need with name=${need.name}`)),
      catchError(this.handleError<any>('updateNeed'))
    );
  }

  /** DELETE: delete the need from the server */
  deleteNeed(name: string): Observable<Needs> {
    const url = `${this.needsUrl}/${name}`;

    return this.http.delete<Needs>(url, this.httpOptions).pipe(
      tap((_) => this.log(`deleted need with name=${name}`)),
      catchError(this.handleError<Needs>('deleteNeed'))
    );
  }

  /** GET needs whose name contains search term */
  searchNeeds(term: string): Observable<Needs[]> {
    if (!term.trim()) {
      return of([]);
    }
    return this.http.get<Needs[]>(`${this.needsUrl}/?name=${term}`).pipe(
      tap((x) => x.length ? this.log(`found needs matching "${term}"`) : this.log(`no needs matching "${term}"`)),
      catchError(this.handleError<Needs[]>('searchNeeds', []))
    );
  }

  /**
   * Handle Http operation that failed.
   * Let the app continue.
   *
   * @param operation - name of the operation that failed
   * @param result - optional value to return as the observable result
   */
  private handleError<T>(operation = 'operation', result?: T) {
    return (error: any): Observable<T> => {
      console.error(error);
      this.log(`${operation} failed: ${error.message}`);
      return of(result as T);
    };
  }

  /** Log a FundingBasketService message with the MessageService */
  private log(message: string) {
    console.log(`FundingBasketService: ${message}`);
  }

  /**
   * Users add a need to the funding basket
   *
   * @param need The need that is being added to the funding basket
   */
   public addSupportedNeed(need: Needs): void {
    if (this.cupboard.getCupboard().contains(need)) {
      this.supportedNeeds.push(need);
    } else {
      console.error('Need is not in the cupboard.');
    }
  }

  /**
   * Users can remove a specific need from the funding basket
   *
   * @param need The need that is being removed from the funding basket
   */
  public removeSupportedNeed(need: Needs): void {
    const index = this.supportedNeeds.indexOf(need);
    if (index !== -1) {
      this.supportedNeeds.splice(index, 1);
    } else {
      console.error('Need is not in the funding basket.');
    }
  }

  /**
   * Users can check out the needs from the funding basket
   */
  public checkOut(): void {
    for (const need of this.supportedNeeds) {
      const name = need.name;
      const cost = need.cost;
      const type = need.type;
      const quantity = need.quantity;
      const updateSuccess = this.cupboard.updateNeed(name, cost, type, quantity - 1);
      if (!updateSuccess) {
        console.error(`Failed to update need ${name} in the cupboard.`);
      }
    }
    this.supportedNeeds = []; 
  }
}
