import { Component } from '@angular/core';
import { FundingBasket } from 'src/app/fundingbasket.service';
import { Needs } from 'src/app/needs';


@Component({
  selector: 'app-helper-page',
  templateUrl: './helper-page.component.html',
  styleUrls: ['./helper-page.component.scss']
})
export class HelperPageComponent {
  fundingBasketService: FundingBasket;
  public needsList: Needs[];
  public searchText: string = '';
  public filteredNeeds: Needs[];

  constructor(public fundingBasket: FundingBasket) {
    this.fundingBasketService = fundingBasket;
    this.needsList = [];
    this.filteredNeeds = [];
  }

  addNeedToBasket(need: Needs): void {
    this.fundingBasket.addSupportedNeed(need);
  }

  removeNeedFromBasket(need: Needs): void {
    this.fundingBasket.removeSupportedNeed(need);
  }


  checkout(): void {
    this.fundingBasket.checkOut();
  }

  ngOnInit(): void {
    this.getAllNeeds();
  }

  getAllNeeds(): void {
    this.fundingBasket.getAllNeeds().subscribe(
      (needs: Needs[]) => {
        this.needsList = needs;
        this.applySearchFilter();
      },
      (error) => {
        console.error('Error fetching needs:', error);
      }
    );
  }

  applySearchFilter(): void {
    this.filteredNeeds = this.needsList.filter(need =>
      need.name.toLowerCase().includes(this.searchText.toLowerCase())
    );
  }

  searchNeeds(searchText: string): void {
    this.searchText = searchText;
    this.applySearchFilter();
  }

}
