import { Component, OnInit } from '@angular/core';
import { AdminService } from 'src/app/admin.service';
import { Needs } from 'src/app/needs';
import { FormGroup, FormBuilder, Validators, FormControl } from '@angular/forms';
import { min } from 'rxjs';

@Component({
  selector: 'app-admin-page',
  templateUrl: './admin-page.component.html',
  styleUrls: ['./admin-page.component.scss']
})
export class AdminPageComponent implements OnInit{
  cupboardNeeds: Needs[] = [];
  form: FormGroup = this.fb.group({
    needName: ['',Validators.required],
    needCost: ['',Validators.required],
    needType: ['',Validators.required],
    needQuant: ['',Validators.required]
  });
  updateForm: FormGroup = this.fb.group({
    updateName: ['',Validators.required],
    updateCost: ['',Validators.required],
    updateType: ['',Validators.required],
    updateQuant: ['',Validators.required]
  });

  constructor(private adminService: AdminService,private fb: FormBuilder){ };

  ngOnInit(): void {
      this.getHeroes();
  }

  getHeroes(): void {
    this.adminService.getAccounts()
    .subscribe(Needs => this.cupboardNeeds = Needs);
  }

  reset(): void{
    this.form.reset();
  }

  add(needName: string, needCost: number, needType:string, needQuant:number): void{
    let needToAdd = this.makeNewNeed(needName,needCost,needType,needQuant);
    this.adminService.addNeed(needToAdd as Needs)
    .subscribe(need => {
      this.cupboardNeeds.push(need);
    });
  }
  delete(need: Needs): void{
    this.cupboardNeeds = this.cupboardNeeds.filter(n => n !== need);
    this.adminService.deleteNeed(need.name).subscribe();
  }
  update(needName: string, needCost: number, needType:string, needQuant:number): void{
    let need = this.makeNewNeed(needName,needCost,needType,needQuant)
  this.cupboardNeeds.forEach(cupNeed => {
    if (cupNeed.name === need.name) {
      let currentIndex = this.cupboardNeeds.indexOf(cupNeed)
      this.adminService.updateNeed(need)
        .subscribe(need => {this.cupboardNeeds[currentIndex]=need});
    }
  });
  }

  makeNewNeed(needName: string, needCost: number, needType:string, needQuant:number): Needs{
    needName = needName.trim();
    needType = needType.trim();
    let quantInt = Math.trunc(needQuant);

    let needToAdd = {} as Needs;

    needToAdd.name = needName;
    needToAdd.cost = needCost;
    needToAdd.type = needType;
    needToAdd.quantity = quantInt;
    return needToAdd;
  }
}
<!doctype html>
<html lang="en">
<head>
  <meta charset="utf-8">
  <title>Admin</title>
  <base href="/admin">
  <meta name="Admin" content="width=device-width, initial-scale=1">

  <link rel="stylesheet" 
          type="text/scss" 
          href="admin-page.component.scss">
</head>

<h2>Admin Cupboard</h2>

<div id="container" [formGroup]="form">
  <!--Need Name input-->
  <label for="new-need">Need name: </label>
  <input type = "text" formControlName="needName" id="new-need" #needName />
  <!--Need Cost input-->
  <label for="new-cost">Need Cost: </label>
  <input type = "number" formControlName="needCost" id="new-cost" #needCost />
  <!--Need Type input-->
  <label for="new-type">Need Type: </label>
  <input type = "text" formControlName="needType" id="new-type" #needType />
  <!--Need Quantity input-->
  <label for="new-quant">Need Quantity: </label>
  <input type = "number" formControlName="needQuant" id="new-quant" #needQuant />

  <!-- (click) passes input value to add() and then clears the input -->
  <button type="button" class="add-button" (click)="add(needName.value,this.form.value.needCost,needType.value,this.form.value.needQuant);
   needName.value='';needCost.value='';needType.value='';needQuant.value=''; reset()" [disabled]="!form.valid">
    Add New Need
  </button>
</div>

<div id="container" [formGroup]="updateForm">
  <!--Need Name input-->
  <label for="new-need">Need to update's name: </label>
  <input type = "text" formControlName="updateName" id="new-need" #needName />
  <!--Need Cost input-->
  <label for="new-cost">New Need Cost: </label>
  <input type = "number" formControlName="UpdateCost" id="new-cost" #needCost />
  <!--Need Type input-->
  <label for="new-type">New Need Type: </label>
  <input type = "text" formControlName="UpdateType" id="new-type" #needType />
  <!--Need Quantity input-->
  <label for="new-quant">Need Quantity: </label>
  <input type = "number" formControlName="UpdateQuant" id="new-quant" #needQuant />

  <!-- (click) passes input value to add() and then clears the input -->
  <button type="button" class="add-button" (click)="update(needName.value,this.form.value.needCost,needType.value,this.form.value.needQuant);
   needName.value='';needCost.value='';needType.value='';needQuant.value=''; reset(this.updateForm)" [disabled]="!updateForm.valid">
    Add New Need
  </button>
</div>


<h2>All Needs</h2>
<ul class="needs-list">
  <li *ngFor="let need of cupboardNeeds">
    <div><span>Name: </span>{{ need.name }}</div>
    <div><span>Cost: </span>{{ need.cost }}</div>
    <div><span>Type: </span>{{ need.type }}</div>
    <div><span>Quantity: </span>{{ need.quantity }}</div>
    <button (click)="delete(need)">Delete Need</button>
  </li>
</ul>
