import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Location } from '@angular/common';

import { Needs } from '../needs';
import { Cupboard } from '../cupboard.service';

@Component({
  selector: 'app-need-detail',
  templateUrl: './need-detail.component.html',
  styleUrls: [ './need-detail.component.scss' ]
})
export class NeedDetailComponent implements OnInit {
  need: Needs | undefined;

  constructor(
    private route: ActivatedRoute,
    private cupboardService: Cupboard,
    private location: Location
  ) {}

  ngOnInit(): void {
    this.getNeed();
  }

  getNeed(): void {
    const name = this.route.snapshot.paramMap.get('name') || '';
    this.cupboardService.getNeed(name)
      .subscribe(need => this.need = need);
  }

  goBack(): void {
    this.location.back();
  }
}
