import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Observable, of } from 'rxjs';
import { catchError, tap } from 'rxjs/operators';
import { Needs } from 'src/app/needs'; 
import { Cupboard } from 'src/app/cupboard.service';

@Injectable({
  providedIn: 'root',
})
export class FundingBasket {
  private needsUrl = 'http://localhost:8080/helper';
  private supportedNeeds: Needs[] = [];

  httpOptions = {
    headers: new HttpHeaders({ 'Content-Type': 'application/json' }),
  };

  constructor(private http: HttpClient) {}

  /** GET needs whose name contains search term */
  searchNeeds(term: string): Observable<Needs[]> {
    if (!term.trim()) {
      return of([]);
    }
    return this.http.get<Needs[]>('${this.needsUrl}/?name=${term}').pipe(
      tap((x) => x.length ? this.log('found needs matching "${term}"') : this.log('no needs matching "${term}"')),
      catchError(this.handleError<Needs[]>('searchNeeds', []))
    );
  }

  // GET request for all supported needs
  getSupportedNeeds(): Observable<Needs[]> {
    const url = `${this.needsUrl}/fundingbasket`;
    return this.http.get<Needs[]>(url).pipe(
      tap((supportedNeeds) => this.log('fetched supported needs')),
      catchError(this.handleError<Needs[]>('getSupportedNeeds', []))
    );
  }

  // POST request to add a new supported need
  addSupportedNeed(need: Needs): Observable<Needs> {
    const url = `${this.needsUrl}/fundingbasket/addneed`;
    return this.http.post<Needs>(url, need, this.httpOptions).pipe(
      tap((addedNeed: Needs) => this.log(`added need with name=${addedNeed.name}`)),
      catchError(this.handleError<Needs>('addSupportedNeed'))
    );
  }

  // DELETE request to remove a supported need
  removeSupportedNeed(name: string): Observable<Needs> {
    const url = `${this.needsUrl}/fundingbasket/${name}`;
    return this.http.delete<Needs>(url, this.httpOptions).pipe(
      tap((_) => this.log(`deleted need with name=${name}`)),
      catchError(this.handleError<Needs>('removeSupportedNeed'))
    );
  }

  // PUT request to update the cupboard after checking out needs
  checkOut(): Observable<Needs> {
    const url = `${this.needsUrl}/fundingbasket/checkout`;
    return this.http.put<Needs>(url, null, this.httpOptions).pipe(
      tap((_) => this.log('checked out supported needs')),
      catchError(this.handleError<Needs>('checkOut'))
    );
  }

  /**
   * Handle Http operation that failed.
   * Let the app continue.
   *
   * @param operation - name of the operation that failed
   * @param result - optional value to return as the observable result
   */
  private handleError<T>(operation = 'operation', result?: T) {
    return (error: any): Observable<T> => {
      console.error(error);
      this.log('${operation} failed: ${error.message}');
      return of(result as T);
    };
  }

   /** Log a FundingBasketService message with the MessageService */
   private log(message: string) {
    console.log('FundingBasketService: ${message}');
  }


}
