import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http'

import { Observable, of } from 'rxjs';
import { catchError, map, tap } from 'rxjs/operators';

import { Account } from './account';

@Injectable({
  providedIn: 'root'
})
export class AuthService {
  //TODO replace with login backend elements
  users: any[] = [
    {
      id: 1,
      name: 'Admin',
      username: 'admin'
    },
    {
      id: 2,
      name: 'Sam',
      username: 'sam'
    },
    {
      id: 3,
      name: 'Kelvin',
      username: 'kelvin'
    },
    {
      id: 4,
      name: 'Helper',
      username: 'helper'
    }
  ];

  private accountsUrl = 'http://localhost:8080/account';  // URL to web api

  httpOptions = {
    headers: new HttpHeaders({ 'Content-Type': 'application/json' })
  };

  session: any;

  constructor(
    //private http: HttpClient
    ) { }

  login(username: string){
    let user = this.users.find((u)=>u.username===username);
    if(user){
      this.session = user;
      localStorage.setItem('session', JSON.stringify(this.session))
    }
    return user;
  }

  createUser(username: string): Observable<Account> {
    return this.http.post<Account>(this.accountsUrl, username, this.httpOptions).pipe(
