import { Component, OnInit } from '@angular/core';
import { HelperMessageService } from 'src/app/helper-message.service';
import { HelperMessage } from 'src/app/HelperMessage';

@Component({
  selector: 'app-landing',
  templateUrl: './landing.component.html',
  styleUrls: ['./landing.component.scss']
})

export class LandingComponent implements OnInit {
  helperMessages: HelperMessage[] = []

  randomMessage: HelperMessage = {} as HelperMessage;

  constructor(private messageService: HelperMessageService) { }

  ngOnInit() {
    this.randomMessage = this.messageService.getRandomMessage();
  }  
}
<!doctype html>
<html lang="en">
<head>
  <meta charset="utf-8">
  <title>Landing</title>
  <base href="/landing">
  <meta name="landing" content="width=device-width, initial-scale=1">

  <link rel="stylesheet" 
          type="text/scss" 
          href="landing.component.scss">

</head>


<div class="textContent">
  <h2> Helper Messages </h2>
  <div class="ticker">
    <h3>{{randomMessage.message}}</h3>
  </div>

</div>

<div id="logincontainer">
  <h1>Funds In High Places - Food Donation Inc.</h1>
  <button routerLink="/loginPage">LOGIN</button>
  <button routerLink="/CreateUser">CREATE</button>
</div>
