import { Component } from '@angular/core';

@Component({
  selector: 'app-helper',
  templateUrl: './helper.component.html',
  styleUrls: ['./helper.component.scss']
})
export class HelperComponent {
    title = 'Helper';

    constructor(public fundingBasket: FundingBasket) {

    }

    addNeedToBasket(need: Needs): void {
      this.fundingBasket.addSupportedNeed(need);
    }

    removeNeedFromBasket(need: Needs): void {
      this.fundingBasket.removeSupportedNeed(need);
    }

    checkout(): void {
      this.fundingBasket.checkOut();
    }

    getAllNeeds(): void {
      this.fundingBasket.getAllNeeds().subscribe({
        next: (needs: Needs[]) => {
          this.needs = needs;
        },
        error: (error) => {
          console.error('Error fetching needs:', error);
        }
      });
    }

}
