import { Component } from '@angular/core';
import { Needs } from 'src/app/needs';
import { FundingBasket } from 'src/app/fundingbasket.service';
import { Cupboard } from 'src/app/cupboard.service';
import { NEEDS } from 'src/app/mock-needs';

@Component({
  selector: 'app-helper-page',
  templateUrl: './helper-page.component.html',
  styleUrls: ['./helper-page.component.scss']
})
export class HelperPageComponent {

  needs: Needs[] = NEEDS;
  selectedNeed?: Needs;

  constructor(
    public fundingBasket: FundingBasket,
    private cupboardService: Cupboard
  ) {}

  toggleBasketDropdown(): void {
    // Implement if needed
  }

  addNeedToBasket(need: Needs): void {
    this.fundingBasket.addSupportedNeed(need);
  }

  removeNeedFromBasket(need: Needs): void {
    this.fundingBasket.removeSupportedNeed(need);
  }

  checkout(): void {
    this.fundingBasket.checkOut();
  }

  getAllNeeds(): void {
    this.fundingBasket.getAllNeeds().subscribe({
      next: (needs: Needs[]) => {
        this.needs = needs;
        this.applySearchFilter();
      },
      error: (error) => {
        console.error('Error fetching needs:', error);
      }
    });
  }

  addNewNeed(): void {
    // Implement if needed
  }

  updateNeed(need: Needs): void {
    // Implement if needed
  }

  deleteNeed(need: Needs): void {
    // Implement if needed
  }

  searchNeeds(searchText: string): void {
    // Implement if needed
  }

  showNeedDetails(need: Needs): void {
    this.selectedNeed = need;
  }
}
<!DOCTYPE html>
<html lang="en">

<head>
  <meta charset="utf-8">
  <title>Helper</title>
  <base href="/helper">
  <meta name="helper" content="width=device-width, initial-scale=1">
  <link rel="stylesheet" type="text/scss" href="helper-page.component.scss">
</head>

<body>
  <div class="container">
    <div class="top-bar">
      <div class="funding-basket-dropdown">
        <button (click)="toggleBasketDropdown()">Funding Basket</button>
        <div *ngIf="showBasketDropdown" class="basket-content">
          <ul>
            <li *ngFor="let item of fundingBasket.getSupportedNeeds()">
              {{ item.name }} - {{ item.quantity }}
            </li>
          </ul>
          <button (click)="checkout()">Checkout</button>
        </div>
      </div>
    </div>

    <div class="search-bar">
      <input [(ngModel)]="searchText" placeholder="Search For A Need..." />
      <button (click)="searchNeeds(searchText)">Search</button>
    </div>

    <div *ngIf="fundingBasket.getSupportedNeeds().length > 0">
      <h2> Supported Needs: </h2>
      <ul>
        <li *ngFor="let need of fundingBasket.getSupportedNeeds()" (click)="showNeedDetails(need)">
          {{ need.name }} - {{ need.quantity }} - <button (click)="removeNeedFromBasket(need)">Remove</button>
        </li>
      </ul>
      <button (click)="checkout()">Checkout</button>
    </div>

    <!-- Space between search bar and list -->
    <div style="margin-top: 20px;"></div>

    <h2> Needs: </h2>
    <ul class="horizontal-list">
      <li *ngFor="let need of needs" (click)="showNeedDetails(need)">
        <button [class.selected]="need === selectedNeed" type="button">
          <span class="name">{{ need.name }}</span>
        </button>
      </li>
    </ul>
  </div>

  <!-- Display details of the selected need -->
  <div *ngIf="selectedNeed">
    <h2>Selected Need Details:</h2>
    <div>Name: {{ selectedNeed.name | uppercase }}</div>
    <div>Cost: {{ selectedNeed.cost | currency:'USD':true }}</div>
    <div>Type: {{ selectedNeed.type }}</div>
    <div>Quantity: {{ selectedNeed.quantity }} units</div>
  </div>
</body>

</html>
