import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http'

import { Observable, of } from 'rxjs';
import { catchError, map, tap } from 'rxjs/operators';

import { Account } from './account';

@Injectable({
  providedIn: 'root'
})
export class AuthService {
  private accountUrl = 'http://localhost:8080/acounts';  // URL to web api
  users: Account[] = [];
  session: any;
  
  httpOptions = {
    headers: new HttpHeaders({ 'Content-Type': 'application/json' })
  };

  constructor(
    ) { }

  getAccounts(): Observable<Account[]>{
    return this.http.get<Account[]>(this.accountUrl)
    .pipe(
      tap(_ => catchError(this.handleError<Account[]>('getHeroes', [])))
    );
  }

  createAccount(username: string): Observable<Account> {
    return this.http.post<Account>(this.accountUrl, username, this.httpOptions).pipe(
      tap((newAccount: Account) => catchError(this.handleError<Account>('addHero'))));
  }

  login(username: string){
    let user = this.users.find((u)=>u.Username===username);
    if(user){
      this.session = user;
      localStorage.setItem('session', JSON.stringify(this.session))
    }
    return user;
  }


  private handleError<T>(operation = 'operation', result?: T) {
    return (error: any): Observable<T> => {

      // TODO: send the error to remote logging infrastructure
      console.error(error); // log to console instead

      // Let the app keep running by returning an empty result.
      return of(result as T);
    };
  }
}
