import { Component, OnInit } from '@angular/core';
import { Needs } from 'src/app/needs';
import { FundingBasket } from 'src/app/fundingbasket.service';
import { NEEDS } from 'src/app/mock-needs';

@Component({
  selector: 'app-helper-page',
  templateUrl: './helper-page.component.html',
  styleUrls: ['./helper-page.component.scss']
})
export class HelperPageComponent implements OnInit {

  needs: Needs[] = NEEDS;
  selectedNeed?: Needs;
  searchTerm: string = '';
  supportedNeeds: Needs[] = [];


  constructor(public fundingBasket: FundingBasket) {
  }

  ngOnInit(): void {
    this.getNeeds();
  }

  showNeedDetails(need: Needs): void {
    this.selectedNeed = need;
  }

  onSelect(need: Needs): void {
    this.selectedNeed = need;
  }

  getNeeds(): void {
    this.fundingBasket.getAllNeeds().subscribe(needs => {
      this.needs = this.searchTerm.trim() ? needs : NEEDS;
    });
  }

  getDescription(need: Needs): string {
    return `${need.name} is a ${need.type} item with a cost of ${need.cost}.`;
  }

  search(): void {
    if (!this.searchTerm.trim()) {
      this.getNeeds();
      return;
    }

    const searchTermLowerCase = this.searchTerm.toLowerCase();
    this.needs = NEEDS.filter(need =>
      need.name.toLowerCase().includes(searchTermLowerCase)
    );
  }

  resetSearch(): void {
    this.searchTerm = '';
    this.getNeeds(); 
  }

  addToBasket(need: Needs): void {
    const existingNeed = this.supportedNeeds.find(n => n.name === need.name);

    if (existingNeed) {
      existingNeed.quantity += 1;
    } else {
      const newNeed = { ...need, quantity: 1 };
      this.supportedNeeds.push(newNeed);
    }

    this.fundingBasket.addSupportedNeed(need);

    need.quantity -= 1;

    const message = `Adding need to the basket`;
    alert(message);
  }

  removeFromBasket(need: Needs): void {
    this.fundingBasket.removeSupportedNeed(need);
    const final_quant = need.quantity;
    if(need.quantity < final_quant){
      need.quantity +=1;
      const message = `Removing need from basket`;
      alert(message);
    }
    else{
      const message = `Unable to remove since not in Funding Basket`;
      alert(message);
    }

  }

}
