import { Component, OnInit } from '@angular/core';
import { Needs } from 'src/app/needs';
import { HelperService } from 'src/app/helper.service';
import { Cupboard } from 'src/app/cupboard.service';

@Component({
  selector: 'app-helper-page',
  templateUrl: './helper-page.component.html',
  styleUrls: ['./helper-page.component.scss']
})
export class HelperPageComponent implements OnInit {

  cupboardNeeds: Needs[] = []; 
  fundingBasket: Needs[] = []; 
  selectedNeed?: Needs;
  searchTerm: string = '';

  constructor(private helperService: HelperService, private cupboardService: Cupboard) {}

  ngOnInit(): void {
    this.getSupportedNeeds();
    this.cupboardService.getCupboardNeedsObservable().subscribe((needs: Needs[]) => {
      this.cupboardNeeds = needs;
    });
  }

  showNeedDetails(need: Needs): void {
    this.selectedNeed = need;
  }

  onSelect(need: Needs): void {
    this.selectedNeed = need;
  }

  getSupportedNeeds(): void {
    this.helperService.getAllNeeds().subscribe(
      needs => {
        this.supportedNeeds = needs;
      },
      error => {
        console.error(error);
      }
    );
  }

  search(): void {
    if (!this.searchTerm.trim()) {
      this.getSupportedNeeds();
      return;
    }

    this.helperService.searchPartialName(this.searchTerm).subscribe(
      needs => {
        this.supportedNeeds = needs;
      },
      error => {
        console.error(error);
      }
    );
  }

  resetSearch(): void {
    this.searchTerm = '';
    this.getSupportedNeeds(); 
  }

  addToBasket(need: Needs): void {
    if (need.quantity > 0) {
      this.helperService.addSupportedNeed(need.name, 1).subscribe(
        addedNeed => {
          this.fundingBasket.push(addedNeed);
          need.quantity -= 1;
          const message = `Adding need to the basket`;
          alert(message);
        },
        error => {
          console.error(error);
        }
      );
    } else {
      const message = `Cannot add need to the basket since the quantity is already at 0.`;
      alert(message);
    }
  }

  removeFromBasket(need: Needs): void {
    this.helperService.removeSupportedNeed(need.name).subscribe(
      _ => {
        const originalNeed = this.cupboardNeeds.find(n => n.name === need.name);
        if (originalNeed) {
          originalNeed.quantity += 1;
          need.quantity -= 1;
          if (need.quantity === 0) {
            const index = this.fundingBasket.indexOf(need);
            if (index !== -1) {
              this.fundingBasket.splice(index, 1);
            }
          }
          const message = `Removing need from basket`;
          alert(message);
        } else {
          const message = `Unable to remove since not in Funding Basket`;
          alert(message);
        }
      },
      error => {
        console.error(error);
      }
    );
  }

  checkout(): void {
    this.helperService.checkOut().subscribe(
      _ => {
        this.fundingBasket = this.fundingBasket.filter(need => need.quantity > 0);
        this.cupboardNeeds = this.cupboardNeeds.filter(need => {
          const basketNeed = this.fundingBasket.find(n => n.name === need.name);
          return need.quantity > 0 || (basketNeed && basketNeed.quantity === 0);
        });
        this.fundingBasket = [];
        const message = 'Checkout Successful!';
        alert(message);
      },
      error => {
        console.error(error);
      }
    );
  }
}
