import { Component } from '@angular/core';
import { AuthService } from '../auth.service';
import { FormGroup, FormBuilder, Validators } from '@angular/forms';
import { Router } from '@angular/router'
import { helperAccount } from '../helperAccount';

@Component({
  selector: 'app-create-user',
  templateUrl: './create-user.component.html',
  styleUrls: ['./create-user.component.scss']
})
export class CreateUserComponent {
  users: helperAccount[] = []
  form: FormGroup = this.fb.group({
  username: ['',Validators.required]
  });
  constructor(private authService: AuthService, private fb: FormBuilder, private router: Router){}
  
  createUser(username: string): void {
    username = username.trim();
    this.authService.createUser({ username } as helperAccount)
    
    }
}

<!doctype html>
<html lang="en">
<head>
  <meta charset="utf-8">
  <title>Create</title>
  <base href="/createUser">
  <meta name="CreateUser" content="width=device-width, initial-scale=1">

  <link rel="stylesheet" 
          type="text/scss" 
          href="create-user.component.scss">
</head>

<div id="container" [formGroup]="form">
  <h1>Create Account!</h1>
  
  <input type="text" formControlName="username" placeholder="New Username" />
  
  <button (click)="createUser(this.form.value.username)" [disabled]="!form.valid">Create</button>
  
  </div>
