import { Component, OnInit } from '@angular/core';
import { FundingBasket } from 'src/app/fundingbasket.service';
import { Needs } from 'src/app/needs';
import { Cupboard } from 'src/app/cupboard.service';
import { NEEDS } from 'src/app/mock-needs';

@Component({
  selector: 'app-helper-page',
  templateUrl: './helper-page.component.html',
  styleUrls: ['./helper-page.component.scss']
})
export class HelperPageComponent implements OnInit {
  public needsList!: Needs[];
  public cupboardNeeds!: Needs[];
  public searchText: string = '';
  public filteredNeeds!: Needs[];
  public newNeed: Needs = { name: '', cost: 0, type: '', quantity: 0 };
  public needs: Needs[] = NEEDS;

  constructor(
    public fundingBasket: FundingBasket,
    private cupboardService: Cupboard
  ) {}

  ngOnInit(): void {
    this.getAllNeeds();
    this.getAllCupboardNeeds();
  }

  addNeedToBasket(need: Needs): void {
    this.fundingBasket.addSupportedNeed(need);
  }

  removeNeedFromBasket(need: Needs): void {
    this.fundingBasket.removeSupportedNeed(need);
  }

  checkout(): void {
    this.fundingBasket.checkOut();
  }

  applySearchFilter(): void {
    this.filteredNeeds = this.needsList.filter((need) =>
      need.name.toLowerCase().includes(this.searchText.toLowerCase())
    );
  }

  getAllCupboardNeeds(): void {
    this.cupboardService.getAllcupboardNeeds().subscribe({
      next: (cupboardNeeds: Needs[]) => {
        this.cupboardNeeds = cupboardNeeds;
      },
      error: (error) => {
        console.error('Error fetching cupboard needs:', error);
      }
    });
  }

  getAllNeeds(): void {
    this.fundingBasket.getAllNeeds().subscribe({
      next: (needs: Needs[]) => {
        this.needsList = needs;
        this.applySearchFilter();
      },
      error: (error) => {
        console.error('Error fetching needs:', error);
      }
    });
  }

  addNewNeed(): void {
    this.cupboardService.createNeed(this.newNeed).subscribe({
      next: (newNeed: Needs) => {
        this.cupboardNeeds.push(newNeed);
        this.newNeed = { name: '', cost: 0, type: '', quantity: 0 };
      },
      error: (error) => {
        console.error('Error creating new need:', error);
      }
    });
  }

  updateNeed(need: Needs): void {
    this.cupboardService.updateNeed(need).subscribe({
      next: (_) => {
        const index = this.cupboardNeeds.findIndex((n) => n.name === need.name);
        if (index !== -1) {
          this.cupboardNeeds[index] = need;
        }
      },
      error: (error) => {
        console.error('Error updating need:', error);
      }
    });
  }

  deleteNeed(need: Needs): void {
    this.cupboardService.deleteNeed(need.name).subscribe({
      next: (_) => {
        this.cupboardNeeds = this.cupboardNeeds.filter((n) => n.name !== need.name);
      },
      error: (error) => {
        console.error('Error deleting need:', error);
      }
    });
  }

  searchNeeds(searchText: string): void {
    this.searchText = searchText;
    this.applySearchFilter();
  }
}
